package main

import (
	"fmt"
	"os"

	claude "github.com/potproject/claude-sdk-go"
)

func main() {
	apiKey := os.Getenv("API_KEY")
	c := claude.NewClient(apiKey)
	m := claude.RequestBodyMessages{
		Model:     "claude-3-opus-20240229",
		MaxTokens: 1024,
		Messages: []claude.RequestBodyMessagesMessages{
			{
				Role: claude.MessagesRoleUser,
				ContentTypeImage: []claude.RequestBodyMessagesMessagesContentTypeImage{
					{
						Source: claude.RequestBodyMessagesMessagesContentTypeImageSource{
							Type:      "base64",
							MediaType: "image/png",
							Data:      "",
						},
					},
				},
			},
		},
	}
	res, err := c.CreateMessages(m)
	if err != nil {
		panic(err)
	}
	fmt.Println(res.Content[0].Text)
	// The image shows a smooth gradient transitioning from a bright, vibrant orange at the bottom to a light blue at the top. The colors blend seamlessly into each other, creating a striking and visually appealing contrast between the warm and cool tones. The simplicity of the gradient allows the colors to be the main focus, showcasing their luminosity and the way they interact with each other. This type of gradient is often used as a background or design element to add depth, interest, and a sense of progression or transition to various digital and print media projects.
}
